mapper.select:
SELECT NVL(MAX(ROWNUM), 0)
FROM board_review
WHERE ref_group=#{ref_group}

SELECT num,title, writer, review, ref_group, rate, regdate
FROM board_review
WHERE num=#{num}

mapper.update:
UPDATE board_review
SET review=#{review}, rate=#{rate}
WHERE num=#{num}

mapper.select:

SELECT ROUND(AVG(rate), 2) AS average_rate
FROM board_review
WHERE title=#{title}

SELECT *
FROM GROUP_REVIEW_TB
WHERE group_num = #{group_num}
AND writer = #{writer}
